#!/bin/bash

# Otomatik Firebase Functions Environment Variables Ayarlama
# Firebase Console'a gidip değerleri yapıştırmak için hazırlanmış script

set -e

ENV_FILE="../.env.local"
if [ ! -f "$ENV_FILE" ]; then
  echo "❌ Hata: .env.local dosyası bulunamadı!"
  exit 1
fi

# Değerleri oku
FIREBASE_PROJECT_ID=$(grep "^FIREBASE_PROJECT_ID=" "$ENV_FILE" | cut -d '=' -f2- | tr -d '"' | tr -d "'")
